DECLARE @nonce VARCHAR(50) = '[TknParams:nonce]';
DECLARE @AppKey VARCHAR(100) = 'E8TwkOyGteHIG24F'
DECLARE @ValueToEncrypt VARCHAR(100) = @nonce + @AppKey;
DECLARE @EncryptedValue VARBINARY(MAX);

-- Use HASHBYTES function to calculate SHA512 hash
SET @EncryptedValue = HASHBYTES('SHA2_512', @ValueToEncrypt);

-- Convert binary value to hexadecimal string
SELECT CONVERT(VARCHAR(MAX), @EncryptedValue, 2) AS Secret, @nonce AS nonce;
